mapper.select:
select

from dsj_dx_all


		AND dx_adtime like #{secTime}" %"


		AND dx_city = #{city}


		and dx_sys = #{sourceType}


		and dx_firp = #{product}


		and dx_firdw = #{dangw}


		and dx_rh = #{ifrh}


		and dx_ifdx = #{ifdx}

select count(1)
from dsj_dx_all



		AND dx_adtime >= CONCAT(date_format(#{secTime},'%y-%m-%d'),'00:00:00') AND dx_adtime<= CONCAT(date_format(#{secTime},'%y-%m-%d'),'23:59:59')



		AND dx_city = #{city}


		and dx_sys = #{sourceType}


		and dx_firp = #{product}


		and dx_firdw = #{dangw}


		and dx_rh = #{ifrh}


		and dx_ifdx = #{ifdx}

mapper.insert:
insert into dsj_dx_all (id, dx_city, dx_dn, 
  dx_ap, dx_sys, dx_inn, 
  dx_rh, dx_rhlx, dx_firp, 
  dx_firdw, dx_ifdx, dx_adtime
  )
values (#{id,jdbcType=INTEGER}, #{dxCity,jdbcType=VARCHAR}, #{dxDn,jdbcType=VARCHAR}, 
  #{dxAp,jdbcType=VARCHAR}, #{dxSys,jdbcType=VARCHAR}, #{dxInn,jdbcType=VARCHAR}, 
  #{dxRh,jdbcType=VARCHAR}, #{dxRhlx,jdbcType=VARCHAR}, #{dxFirp,jdbcType=VARCHAR}, 
  #{dxFirdw,jdbcType=VARCHAR}, #{dxIfdx,jdbcType=VARCHAR}, #{dxAdtime,jdbcType=TIMESTAMP}
  )
