mapper.select:
SELECT qy_id,qy_name,dn,source,a.addtime,zs_time,zs_state FROM tb_equity_data a, tb_equity_result b
and a.dn = b.cj_dn
and a.dn = #{dn}
GROUP BY a.dn,b.cj_dn UNION SELECT qy_id,qy_name,dn,source,c.addtime,NULL,NULL FROM tb_equity_data c
AND c.dn not in (SELECT cj_dn FROM tb_equity_result)
AND c.dn = #{dn}
GROUP BY c.dn ORDER BY addtime LIMIT #{start},#{limit}

mapper.insert:
insert into tb_equity_data
(id, qy_id, dn,
qy_name, source, addtime
)
values
(#{id,jdbcType=INTEGER}, #{qyId,jdbcType=VARCHAR},
#{dn,jdbcType=VARCHAR},
#{qyName,jdbcType=VARCHAR},
#{source,jdbcType=INTEGER}, #{addtime,jdbcType=TIMESTAMP}
)
